mapper.select:
SELECT a.*, b.CostCenterDesc FROM MACE_REQ_ER_PROCEDURE a, [PROCEDURE] b
WHERE
a.ProcedureId = b.ProcedureId AND a.MACEREQUEST_ID = #{requestId}

SELECT * FROM ( SELECT COUNT(*) OVER ( ) AS totalSize, ROW_NUMBER() OVER (ORDER BY row_.REQUEST_DATETIME desc) AS pageNo, row_.* FROM (
SELECT mr.REQUEST_ID, mrer.TRANS_CODE, mr.REQUEST_DATETIME, mr.REQUEST_BY, mr.MEMBER_CODE, mr.MEM_LNAME, mr.MEM_FNAME, mrer.ER_REASON_REMARKS, mrer.PrimaryDiagnosisId, mrer.PRIMARY_DIAGNOSIS_ICD10, mrer.PRIMARY_DIAGNOSIS_DESC, mrer.ESTIMATED_COST, mr.STATUS, mr.REQUEST_FROMHOSP, syshl.HOSPITAL_NAME FROM MACEREQ_ER mrer INNER JOIN MACE_REQUEST mr ON mrer.MACEREQUEST_ID = mr.REQUEST_ID INNER JOIN SYS_HOSPITALS_LTBL syshl ON mrer.HOSPITAL_CODE = syshl.HOSPITAL_CODE -- WHERE mr.STATUS = 'SUBMITTED'
) row_ ) tablename WHERE pageNo BETWEEN ((#{pageNo}-1) * #{size}+1) AND (#{pageNo})*#{size}

SELECT * FROM ( SELECT COUNT(*) OVER ( ) AS totalSize, ROW_NUMBER() OVER (ORDER BY row_.REQUEST_DATETIME desc) AS pageNo, row_.* FROM (
SELECT mr.REQUEST_ID, mrer.TRANS_CODE, mr.REQUEST_DATETIME, mr.REQUEST_BY, mr.MEMBER_CODE, mr.MEM_LNAME, mr.MEM_FNAME, mrer.ER_REASON_REMARKS, mrer.PrimaryDiagnosisId, mrer.PRIMARY_DIAGNOSIS_ICD10, mrer.PRIMARY_DIAGNOSIS_DESC, mrer.ESTIMATED_COST, mr.STATUS, mr.REQUEST_FROMHOSP, syshl.HOSPITAL_NAME FROM MACEREQ_ER mrer INNER JOIN MACE_REQUEST mr ON mrer.MACEREQUEST_ID = mr.REQUEST_ID INNER JOIN SYS_HOSPITALS_LTBL syshl ON mrer.HOSPITAL_CODE = syshl.HOSPITAL_CODE WHERE CAST(mr.REQUEST_DATETIME AS DATE) >= CAST(#{dateFrom} AS DATE) AND CAST(mr.REQUEST_DATETIME AS DATE) <= DATEADD(day, 1, CAST(#{dateTo} AS DATE)) AND mrer.HOSPITAL_CODE LIKE #{selectedHospitalCode}
) row_ ) tablename WHERE pageNo BETWEEN ((#{pageNo}-1) * #{size}+1) AND (#{pageNo})*#{size}

mapper.update:
        UPDATE [MACEREQ_ER]
        SET [REQUEST_FROM] = #{mrer.requestFrom, jdbcType=VARCHAR}
        ,[STATUS] = #{mrer.status, jdbcType=VARCHAR}
		,[STATUS_REMARKS] = #{mrer.statusRemarks, jdbcType=VARCHAR}
