        ,[APPROVED_BY] = #{mrer.approvedBy, jdbcType=VARCHAR}
        ,[APPROVAL_REMARKS] = #{mrer.approvalRemarks, jdbcType=VARCHAR}
        ,[LOA_NO] = #{mrer.loaNo, jdbcType=VARCHAR}
        ,[ADMITTED_ON] = #{mrer.admittedOn, jdbcType=TIMESTAMP}
        ,[RECORD_SUBMITTED_ON] = #{mrer.recordSubmittedOn, jdbcType=TIMESTAMP}
        ,[RECORD_SUBMITTED_BY] = #{mrer.recordSubmittedBy, jdbcType=VARCHAR}
        ,[ER_REASON_REMARKS] = #{mrer.erReasonRemarks, jdbcType=VARCHAR}
        ,[HOSPITAL_CODE] = #{mrer.hospitalCode, jdbcType=VARCHAR}
		,[ESTIMATED_COST] = #{mrer.estimatedCost, jdbcType=NUMERIC}
        ,[PrimaryDiagnosisId] = #{mrer.primaryDiagnosisId, jdbcType=INTEGER}
        ,[PRIMARY_DIAGNOSIS_ICD10] = #{mrer.primaryDiagnosisIcd10, jdbcType=VARCHAR}
        ,[PRIMARY_DIAGNOSIS_DESC] = #{mrer.primaryDiagnosisDesc, jdbcType=VARCHAR}
        ,[DX_REMARKS] = #{mrer.dxRemarks, jdbcType=VARCHAR}
        ,[DISCHARGED_ON] = #{mrer.dischargedOn, jdbcType=TIMESTAMP}
        ,[DISCHARGEUPDATE_BY] = #{mrer.dischargeupdateBy, jdbcType=VARCHAR}
        ,[LASTUPDATE_ON] = #{mrer.lastupdateOn, jdbcType=TIMESTAMP}
        ,[LASTUPDATE_BY] = #{mrer.lastupdateBy, jdbcType=VARCHAR}
        WHERE MACEREQUEST_ID = #{mrer.maceRequestId, jdbcType=INTEGER}

